CREATE  FORCE VIEW "SYSADM"."BSCS_USER_SESSION_TRACE" ("USERNAME", "SID", "SERIAL#", "AUDSID", "SCHEMA_NAME", "OSUSER", "PROCESS", "SERVER_PROCESS", "INSTANCE_NAME", "MACHINE", "TERMINAL", "PROGRAM", "TYPE", "MODULE", "ACTION", "CLIENT_IDENTIFIER", "CLIENT_INFO", "LOGON_TIME", "FAILOVER_TYPE", "FAILOVER_METHOD", "FAILED_OVER", "TRACE_FILE_NAME", "PREV_SQL_TEXT", "PREV_SQL_SORTS", "PREV_SQL_FETCHES", "PREV_SQL_EXECUTIONS", "PREV_SQL_PARSE_CALLS", "PREV_SQL_DISK_READS", "PREV_SQL_BUFFER_GETS", "PREV_SQL_ROWS_PROCESSED", "PREV_SQL_OPTIMIZER_MODE", "PREV_SQL_CPU_TIME", "PREV_SQL_ELAPSED_TIME", "PREV_SQL_VERSION_COUNT", "PREV_SQL_LOADED_VERSIONS", "PREV_SQL_OPEN_VERSIONS", "PREV_SQL_USERS_OPENING", "PREV_SQL_LOADS", "PREV_SQL_FIRST_TIME", "PREV_SQL_AVE_CPU_TIME", "PREV_SQL_AVE_ELAPSED_TIME", "PREV_SQL_AVE_DISK_READS", "PREV_SQL_PER_MEM", "PREV_SQL_RUN_MEM", "PREV_SQL_AVE_SORTS", "PREV_SQL_AVE_PARSE_CALLE", "PREV_SQL_AVE_BUFFER_GETS", "PREV_SQL_AVE_ROW_PROC", "PREV_SQL_AVE_SER_ABORTS") AS 
  select s.username
      ,s.sid
      ,s.serial#
      ,s.audsid
      ,s.schemaname      SCHEMA_NAME
      ,s.osuser
      ,s.process
      ,p.spid            SERVER_PROCESS
      ,i.value           INSTANCE_NAME
      ,s.machine
      ,s.terminal
      ,s.program
      ,s.type
      ,s.module
      ,s.action
      ,s.client_identifier
      ,s.client_info
      ,s.logon_time
      ,s.failover_type
      ,s.failover_method
      ,s.failed_over
      ,u.value   || '/' ||  i.value || '_ora_' ||  p.spid  ||  nvl2(p.traceid,'_' ||   p.traceid, null ) ||  '.trc' Trace_File_Name
      ,q.SQL_TEXT                                           PREV_SQL_TEXT
      ,q.SORTS                                              PREV_SQL_SORTS
      ,q.FETCHES                                            PREV_SQL_FETCHES
      ,q.EXECUTIONS                                         PREV_SQL_EXECUTIONS
      ,q.PARSE_CALLS                                        PREV_SQL_PARSE_CALLS
      ,q.DISK_READS                                         PREV_SQL_DISK_READS
      ,q.BUFFER_GETS                                        PREV_SQL_BUFFER_GETS
      ,q.ROWS_PROCESSED                                     PREV_SQL_ROWS_PROCESSED
      ,q.OPTIMIZER_MODE                                     PREV_SQL_OPTIMIZER_MODE
      ,q.CPU_TIME                                           PREV_SQL_CPU_TIME
      ,q.ELAPSED_TIME                                       PREV_SQL_ELAPSED_TIME
      ,q.version_count                                      PREV_SQL_VERSION_COUNT
      ,q.loaded_versions                                    PREV_SQL_LOADED_VERSIONS
      ,q.open_versions                                      PREV_SQL_OPEN_VERSIONS
      ,q.users_opening                                      PREV_SQL_USERS_OPENING
      ,q.loads                                              PREV_SQL_LOADS
      ,q.first_load_time                                    PREV_SQL_FIRST_TIME
      ,ceil(q.cpu_time/greatest(q.executions,1))            PREV_SQL_AVE_CPU_TIME
      ,ceil(q.elapsed_time/greatest(q.executions,1))        PREV_SQL_AVE_ELAPSED_TIME
      ,ceil(q.disk_reads/greatest(q.executions,1))          PREV_SQL_AVE_DISK_READS
      ,q.persistent_mem                                     PREV_SQL_PER_MEM
      ,q.runtime_mem                                        PREV_SQL_RUN_MEM
      ,ceil(q.sorts/greatest(q.executions,1))               PREV_SQL_AVE_SORTS
      ,ceil(q.parse_calls/greatest(q.executions,1))         PREV_SQL_AVE_PARSE_CALLE
      ,ceil(q.Buffer_gets/greatest(q.executions,1))         PREV_SQL_AVE_BUFFER_GETS
      ,ceil(q.rows_processed/greatest(q.executions,1))      PREV_SQL_AVE_ROW_PROC
      ,ceil(q.Serializable_aborts/greatest(q.executions,1)) PREV_SQL_AVE_SER_ABORTS
from  v$parameter u,
      v$parameter i,
      v$process p,
      v$sqlarea q,
      v$session s
where u.name            = 'user_dump_dest'
and   i.name            = 'instance_name'
and   s.audsid          = sys_context('userenv','sessionid')
and   s.paddr           = p.addr
and   s.PREV_SQL_ADDR   = q.address    (+)
and   s.PREV_HASH_VALUE = q.hash_value (+)
 ;
 
