/*
** =====================================================================
**
** Procedure: CheckTableList
** Purpose: Private procedure for checking the existance of tables
**          to be altered.
** =====================================================================
*/
  PROCEDURE CheckTableList (
--  List of table names
    piasTableName   IN  GEN_UDR_SERV.tasText

--  Indicates if it is needed to check whether a table partitioned or not
   ,piobCheckPartit IN  BOOLEAN DEFAULT TRUE
                           )
  IS
    losTName               VARCHAR2(30);
    losPartitioned         TABS.PARTITIONED%TYPE;
  BEGIN
    FOR ind IN piasTableName.FIRST..piasTableName.LAST
    LOOP
      BEGIN
        BEGIN
          SELECT TNAME
            INTO losTName
            FROM TAB
           WHERE TNAME = piasTableName(ind)
             AND TABTYPE = 'TABLE';
        EXCEPTION
          WHEN NO_DATA_FOUND
          THEN
            NULL;
        END;

        IF losTName IS NULL
        THEN
          BEGIN
            SELECT TABLE_NAME
              INTO losTName
              FROM ALL_SYNONYMS
             WHERE SYNONYM_NAME = piasTableName(ind)
               AND TABLE_OWNER = USER;
        EXCEPTION
          WHEN NO_DATA_FOUND
          THEN
            CallError( 'Input is wrong. Table or synonym ' || piasTableName(ind) || ' does not exist.');
          END;
        END IF;

        IF piobCheckPartit
        THEN
          SELECT PARTITIONED
            INTO losPartitioned
            FROM TABS
           WHERE TABLE_NAME = losTName;

          IF losPartitioned <> 'YES'
          THEN
            CallError( 'Input is wrong. Table ' || losTName || ' is not partitioned.');
          END IF;
        END IF;

      EXCEPTION
        WHEN NO_DATA_FOUND
        THEN
          CallError( 'Input is wrong. Table ' || losTName || ' does not exist.');
      END;
    END LOOP;
  EXCEPTION
    WHEN OTHERS
    THEN
      CallError('Check the table name list failed.');
  END CheckTableList;

/*
** =====================================================================
**
** Function: GetTableName
** Purpose: This function returns a table name which is pointed
**          by a given synonym.
** =====================================================================
*/
  FUNCTION GetTableName(
--  Synonym name
    piosSynonymName  ALL_SYNONYMS.SYNONYM_NAME%TYPE
                       )
  RETURN SYN.TABLE_NAME%TYPE
  IS
    losTName  ALL_SYNONYMS.TABLE_NAME%TYPE;
  BEGIN
    SELECT TABLE_NAME
    INTO losTName
    FROM ALL_SYNONYMS
    WHERE SYNONYM_NAME = piosSynonymName
      AND TABLE_OWNER = USER;
    RETURN losTName;
  EXCEPTION
    WHEN NO_DATA_FOUND
    THEN
      CallError( 'Input is wrong. There is no table for synonym ' || piosSynonymName || '.');
  END GetTableName;

/*
** =====================================================================
**
** Function: SubstrTableName
** Purpose:  This private function returns the table name to be used
**           by storage parameters obtaining.
** =====================================================================
*/
  FUNCTION SubstrTableName(
--  Source string
    piosSourceString  varchar2
                       )
  RETURN varchar2
  IS
    losTableName  varchar2(30);
  BEGIN
    losTableName := substr(
                            regexp_substr(
		                                       piosSourceString
                                          ,'[[:space:]][^[:space:],)]+'
		                                     )
                           ,2
                          );
    return losTableName;
  end SubstrTableName;

/*
** =====================================================================
**
** Function: SubstrSeqNum
** Purpose:  This private function returns the sequence number to be used
**           by storage parameters obtaining.
** =====================================================================
*/
  FUNCTION SubstrSeqNum(
--  Source string
    piosSourceString  varchar2
                       )
  RETURN integer
  IS
    lonSeqNum  integer;
  BEGIN
    lonSeqNum := substr(
                         regexp_substr(
		                                    piosSourceString
				                               ,'[[:space:]][[:digit:]]+[^,)]?'
						                          )
                        ,2
                       );
    return lonSeqNum;
  end SubstrSeqNum;
