/*
** =====================================================================
**
** Procedure: CreateTable
** Purpose: This procedure creates a table according to a context key
**          or returns a corresponding statement to the STDOUT.
** =====================================================================
*/
   PROCEDURE CreateTable (
--  Context key
    piosContextKey         VARCHAR2

--  Table name
   ,piosTableName          VARCHAR2 DEFAULT NULL

--  Automatic/Manual mode
--  TRUE - An SQL statement is executed
--  FALSE - Output of an SQL statement (without executing)
   ,piobExecute            BOOLEAN     DEFAULT FALSE
                         )
  IS
    lasDDLStatements       GEN_UDR_SERV.tasDDLStatements;
  BEGIN
    --  Input parameter's check
    IF piosContextKey IS NULL and piosTableName is null
    THEN
      CallError( 'Either context key or table name must be provided.' );
    END IF;

    --  Generate SQL statements for the table,
    --  constraints, indexes and comments creating
    GEN_UDR_SERV.CreateDDL (
			                       piosContextKey       => UPPER(piosContextKey)
			                      ,piosTableName        => UPPER(piosTableName)
                            ,poasDDLStatements    => lasDDLStatements
                           );

    --  Apply generated statement for table creating
    ApplyDDLString (
                      piasDDLStatements => lasDDLStatements
                     ,piobExecute  => piobExecute
                   );
  EXCEPTION
	   WHEN OTHERS
	   THEN
	      CallError('CreateTable Table: ' || UPPER(piosTableName) || ' failed.');
  END CreateTable;

/*
** =====================================================================
**
** Procedure: ApplyDDLString
** Purpose: This procedure executes dynamically generated SQL statement
**          or returns this statement to the STDOUT.
** =====================================================================
*/
  PROCEDURE ApplyDDLString (
--  Generated DDL Statements
    piasDDLStatements  IN  GEN_UDR_SERV.tasDDLStatements

--  Automatic/Manual mode
--  TRUE - An SQL statement is executed
--  FALSE - Output of an SQL statement (without executing)
   ,piobExecute        IN  BOOLEAN
                           )
  IS
    lonFinish              BINARY_INTEGER;
    lonStart               BINARY_INTEGER := 1;
    cursor_id              BINARY_INTEGER;
    ret_val                BINARY_INTEGER;
    lasDDL                 DBMS_SQL.VARCHAR2S;
    losTmp                 varchar2(256);

    losTableName           varchar2(30);
    lonSeqNum              integer;
    losPartitName          varchar2(30);
  BEGIN
    IF piasDDLStatements.COUNT > 0
    THEN
      cursor_id := DBMS_SQL.OPEN_CURSOR;

        --  Some replacements in the DDL statements to be executed.
        FOR StatementInd IN piasDDLStatements.FIRST..piasDDLStatements.LAST
        LOOP
          FOR StringInd IN piasDDLStatements(StatementInd).FIRST..piasDDLStatements(StatementInd).LAST
          LOOP
            lasDDL(StringInd) := piasDDLStatements(StatementInd)(StringInd);
            case
              when regexp_like(lasDDL(StringInd), ':TableStorage')
              then
                losTmp := regexp_substr(lasDDL(StringInd),':TableStorage .*');
                losTableName := SubstrTableName(losTmp);
                lasDDL(StringInd) := regexp_replace(
                                                lasDDL(StringInd)
                                               ,':TableStorage[^,)]*'
			                                         ,Migration.TableStorage(losTableName)
                                                   );
              when regexp_like(lasDDL(StringInd), ':PkeyStorage')
              then
                losTmp := regexp_substr(lasDDL(StringInd),':PkeyStorage .*');
                losTableName := SubstrTableName(losTmp);
                lasDDL(StringInd) := regexp_replace(
                                                lasDDL(StringInd)
                                               ,':PkeyStorage[^,)]*'
			                                         ,Migration.PkeyStorage(losTableName)
                                                   );
              when regexp_like(lasDDL(StringInd), ':PartitionStorage')
              then
                losTmp := regexp_substr(lasDDL(StringInd),':PartitionStorage .*');
                losTableName := SubstrTableName(losTmp);
                lonSeqNum := SubstrSeqNum(losTmp);
                lasDDL(StringInd) := regexp_replace(
                                                lasDDL(StringInd)
                                               ,':PartitionStorage[^,)]*'
			                                         ,Migration.PartitionStorage(losTableName, lonSeqNum)
			                                             );
              when regexp_like(lasDDL(StringInd), ':UkeyStorage')
              then
                losTmp := regexp_substr(lasDDL(StringInd),':UkeyStorage .*');
                losTableName := SubstrTableName(losTmp);
                lonSeqNum := SubstrSeqNum(losTmp);
                lasDDL(StringInd) := regexp_replace(
                                                lasDDL(StringInd)
                                               ,':UkeyStorage[^,)]*'
			                                         ,Migration.UkeyStorage(losTableName, lonSeqNum)
			                                             );
              when regexp_like(lasDDL(StringInd), ':IndexStorage')
              then
                losTmp := regexp_substr(lasDDL(StringInd),':IndexStorage .*');
                losTableName := SubstrTableName(losTmp);
                lonSeqNum := SubstrSeqNum(losTmp);
                lasDDL(StringInd) := regexp_replace(
                                                lasDDL(StringInd)
                                               ,':IndexStorage[^,)]*'
			                                         ,Migration.IndexStorage(losTableName, lonSeqNum)
			                                             );
              else
                null;
            end case;

            IF piobExecute = TRUE
            THEN
              IF INSTR(piasDDLStatements(StatementInd)(StringInd),'/',1,1) = 1
              THEN
                lonFinish := StringInd - 1;

                BEGIN
                  DBMS_SQL.PARSE(
                                  cursor_id
                                 ,lasDDL
                                 ,lonStart
                                 ,lonFinish
                                 ,TRUE
                                 ,DBMS_SQL.NATIVE
                                );
                EXCEPTION
                  WHEN seoLocalIndex
                  THEN
                    CallError('The table ' || losTableName || ' is not partitioned, therefore it is not permitted to create a local index on it. Please, modify the UDS setup accordingly.');
                  WHEN seoPrimKeyStorage
                  THEN
                    CallError('The table ' || losTableName || ' is index organized, therefore the storage specification for the primary key is not permitted. Please, update the table MIG_PARAMETER accordingly.');
                  WHEN seoDupObject
                  THEN
                    CallError('Object ' || losTableName || ' already exists.');
                  WHEN seoPartitionBound
                  THEN
                    losPartitName := regexp_substr(
                                                    lasDDL(2)
                                                   ,'[[:space:]][^[:space:]]+'
                                                   ,1
                                                   ,2
		                                              );
                    CallError('Set up value(s) for partition bound must collate higher than that of the last partition. ' || losPartitName || ' is not created.');
                  WHEN seoPartitionValue
                  THEN
                    losPartitName := regexp_substr(
                                                    lasDDL(2)
                                                   ,'[[:space:]][^[:space:]]+'
                                                   ,1
                                                   ,2
		                                              );
                    CallError('List value for the partition ' || losPartitName || ' already exists. ' || losPartitName || ' is not created.');
                  WHEN seoNoTable
                  THEN
                    DBMS_SQL.CLOSE_CURSOR(cursor_id);
                    CallError('Table ' || losTableName || ' does not exist.');
                  WHEN OTHERS
                  THEN
                    DBMS_SQL.CLOSE_CURSOR(cursor_id);
                    CallError( 'Parse cursor for dynamic SQL' );
                END;
                  ret_val := DBMS_SQL.EXECUTE(cursor_id);
                  CallInfo('SQL statement ' || lasDDL(1) || '... has been successfully executed.');
              END IF;
            ELSE
              DBMS_OUTPUT.PUT_LINE(lasDDL(StringInd));
            END IF;
          END LOOP;
          lasDDL.DELETE;
        END LOOP;
      DBMS_SQL.CLOSE_CURSOR(cursor_id);
    END IF;
  EXCEPTION
    WHEN OTHERS
    THEN
      CallError('Apply DDL statement failed');
  END ApplyDDLString;

/*
** =====================================================================
**
** Procedure: AlterTable
** Purpose: This procedure alters a table according to a context key
**          or returns corresponding statements to the STDOUT.
** =====================================================================
*/
  PROCEDURE AlterTable (
--  Context key
    piosContextKey        VARCHAR2

--  Drop column indicator.
--  TRUE - generates/executes SQL statement to set unused
--         the columns from the given table missing in
--         the column list for the given context key
--  FALSE - SQL statement to set any columns unused is
--           by no means generated/executed.
   ,piobDropColumn        BOOLEAN  DEFAULT FALSE

--  Automatic/Manual mode
--  TRUE - An SQL statement is executed
--  FALSE - Output of an SQL statement (without executing)
   ,piobExecute           BOOLEAN     DEFAULT FALSE
                       )
  IS
    TYPE torTablePartitHighValue IS RECORD (
                                             HighValue         USER_TAB_PARTITIONS.HIGH_VALUE%TYPE
                                            ,Length            USER_TAB_PARTITIONS.HIGH_VALUE_LENGTH%type
                                           );

    losTName               ALL_SYNONYMS.TABLE_NAME%TYPE;
    lasDDLStatements       GEN_UDR_SERV.tasDDLStatements;
    lasColumnAttr          GEN_UDR_SERV.tarTableColumnAttr;
    lrGrTabPartitHighValue torTablePartitHighValue;
    lasTabSubpartitAttr    GEN_UDR_SERV.tarTableSubpartitAttr;
    lnCreate               integer;
    lsPartitNames          varchar2(2000);
    lsSQL                  varchar2(2000);
    lasPartitToBeAltered   GEN_UDR_SERV.tarPartitToBeAltered;
    lnIndex                binary_integer := 0;
    lbPartitionedTable     boolean := true;

  BEGIN
    --  Check presence of mandatory input parameter
    IF piosContextKey IS NULL
    THEN
      CallError( 'Input parameter is NULL.' );
    END IF;

    --  Get table name, that is pointed by a synonym,
    --  whose name is identical to a given context key.
    losTName := GetTableName(UPPER(piosContextKey));

    --  Get an array of columns for the given table
    begin
      SELECT  COLUMN_NAME
             ,REPLACE(DATA_TYPE,'NUMBER','INTEGER')
             ,DATA_LENGTH
      BULK COLLECT INTO lasColumnAttr
      FROM USER_TAB_COLUMNS
      WHERE TABLE_NAME = losTName
      ORDER BY COLUMN_NAME;
    exception
      when others then
        CallError('Select from USER_TAB_COLUMNS failed.');
    end;

    --  Get the value of the highest partition for the given table
    begin
      SELECT  HIGH_VALUE
             ,HIGH_VALUE_LENGTH
      INTO lrGrTabPartitHighValue
      FROM USER_TAB_PARTITIONS
      WHERE TABLE_NAME = losTName
      AND (TABLE_NAME, PARTITION_POSITION) = (select TABLE_NAME, max (PARTITION_POSITION)
                                              from USER_TAB_PARTITIONS
                                              WHERE TABLE_NAME = losTName
                                              group by TABLE_NAME);
    exception
      when NO_DATA_FOUND then
        lbPartitionedTable := false;
        CallInfo('The table ' || losTName || ' is not partitioned.');
      when others then
        CallError('Select from USER_TAB_PARTITIONS failed.');
    end;

    if lbPartitionedTable
    then
      --  Get a list of partitions for the given table, which have to be varified for
      --  the presence of all subpartitions currently defined in UDS setup.
      for rec in (select  PARTITION_NAME
                         ,HIGH_VALUE
                  from USER_TAB_PARTITIONS
                  where TABLE_NAME = losTName)
      loop
        begin
          execute immediate 'select decode(greatest(sysdate,' || rec.HIGH_VALUE || '), sysdate, 0, 1) from dual'
          into lnCreate;
        exception
          when others then
            CallInfo('There are no partitions for the table ' || losTName ||
                     ' with high value greater or equal to ' || to_char(sysdate, 'dd.month.yyyy'));
        end;
        if lnCreate =1
        then
          lsPartitNames := lsPartitNames || '''' || rec.PARTITION_NAME || ''',';
          lnIndex := lnIndex + 1;
          lasPartitToBeAltered(lnIndex) := rec.PARTITION_NAME;
        end if;
      end loop;
      lsPartitNames := rtrim (lsPartitNames,',');

      if lsPartitNames is not null
      then
        --  Get a list of subpartition names with their high values for
        --  the given partition and given table
        lsSQL := 'select  PARTITION_NAME
                         ,SUBPARTITION_NAME
                         ,HIGH_VALUE
                         ,HIGH_VALUE_LENGTH
                  from USER_TAB_SUBPARTITIONS
                  where TABLE_NAME = ''' || losTName || '''
                  and PARTITION_NAME in (' || lsPartitNames ||
                  ') order by PARTITION_NAME, SUBPARTITION_POSITION';
        begin
          execute immediate lsSql
          bulk collect into lasTabSubpartitAttr;
        exception
          when NO_DATA_FOUND then
            CallInfo('The table ' || losTName || ' is not subpartitioned.');
          when others then
            CallError('Select from USER_TAB_SUBPARTITIONS failed.');
        end;
      end if;
    end if;

    --  Generate SQL statements for table altering
  GEN_UDR_SERV.AlterDDL(
                           piosTableName            => losTName
                          ,piasTableColumnAttr      => lasColumnAttr
                          ,piosGrTabPartitHighValue => substr(lrGrTabPartitHighValue.HighValue,1,lrGrTabPartitHighValue.Length)
                          ,piasPartitToBeChecked    => lasPartitToBeAltered
                          ,piasTabSubpartitAttr     => lasTabSubpartitAttr
                          ,piobDropColumn           => piobDropColumn
                          ,poasDDLStatements        => lasDDLStatements
                         );

    IF lasDDLStatements.COUNT > 0
    THEN
      --  Apply generated statement for table creating
      ApplyDDLString (
                        piasDDLStatements => lasDDLStatements
                       ,piobExecute  => piobExecute
                     );
      IF piobExecute
      THEN
        DBMS_OUTPUT.PUT_LINE(RPAD('*',80,'*'));
        CallInfo('Table ' || losTName || ', that is pointed by public synonym '
                 || UPPER(piosContextKey) || ' is altered.');
      END IF;
    ELSE
      CallInfo('The structure of the table ' || losTName
                || ' is identical to the structure, which corresponds to the context key '
                || UPPER(piosContextKey) || '.' );
    END IF;

  END AlterTable;

/*
** =======================================================================
**
** Procedure: CreateView_UDR
** Purpose: This procedure creates global UDR_LT_ST or BCH_UDR_LT_ST
**
** Note: This procedure creates either
**
**        view UDR_LT_ST          or
**        view BCH_UDR_LT_ST
**
**        or creating a script of the view. It depends on parameter piobExecute.
** =======================================================================
*/
  PROCEDURE CreateView_UDR (
--  View number
--    1: view UDR_LT_ST
--    2: view BCH_UDR_LT_ST
    pionViewNumber   IN   INTEGER
--  Automatic/Manual mode
--  TRUE - An SQL statement is executed
--  FALSE - Output of an SQL statement (without executing)
   ,piobExecute         BOOLEAN     DEFAULT FALSE

--  Value of this parameter has to indicate whether the view
--  should be based on the all tables marked with 'X' in
--  UDS_TABLES.VIEW_RELEVANCE (TRUE) or it should be based only on those
--  objects, that are marked with 'X' in UDS_TABLES.VIEW_RELEVANCE and
--  actually have at least one public synonym with the same name as the name
--  of one of defined context keys. (FALSE)
   ,piobAllObjects BOOLEAN DEFAULT FALSE
                                )
  IS
    lasName             GEN_UDR_SERV.tasText;
    lasNameTmp          GEN_UDR_SERV.tasText;
    lasFUTableName      GEN_UDR_SERV.tasText;
    lasFUTableNameTmp   GEN_UDR_SERV.tasText;
    lasContextKey       GEN_UDR_SERV.tasText;

    lasDDLStatements    GEN_UDR_SERV.tasDDLStatements;
    lasDynamicString    GEN_UDR_SERV.tasText;
    lonIndex            PLS_INTEGER;

    losSQL              VARCHAR2(2000);
    cursor_id           BINARY_INTEGER;
    ret_val             BINARY_INTEGER;
    lasBaseNames        DBMS_SQL.Varchar2_Table;

  BEGIN
    --  Get the list of defined context keys and the list of tables,
    --  which are flaged as view relevant.
    IF pionViewNumber = 1 OR
       pionViewNumber = 2
    THEN
        GEN_UDR_SERV.GetUDSTables(
                               pionViewNumber  => pionViewNumber
                              ,poasName        => lasName
                              ,poasFUTableName => lasFUTableName
                              ,poasContextKey  => lasContextKey
                             );
    ELSE
        CallError('Create view failed! Wrong call of procedure CreateView_UDR.');
    END IF;

    --  Check the presence of tables to be used as base tables
    --  for the created view.
    CheckTableList(
                    piasTableName   => lasName
                   ,piobCheckPartit => FALSE
                  );

    IF piobAllObjects = FALSE
    THEN
      -- Get an array of table names the created view will be based on.
      -- These tables not only marked as view related, but they actually
      -- have at least one public synonym with the same name as the name
      -- of one of defined context keys.
      losSQL := 'SELECT DISTINCT TABLE_NAME
                 FROM ALL_SYNONYMS
                 WHERE OWNER = ''PUBLIC''
                 AND TABLE_OWNER = USER
                 AND SYNONYM_NAME IN (';
      FOR ind IN lasContextKey.FIRST..lasContextKey.LAST
      LOOP
        losSQL := losSQL || '''' || lasContextKey(ind) || ''',';
      END LOOP;
      losSQL := RTRIM(losSQL,',') || ')' || ' AND TABLE_NAME IN (';
      FOR ind IN lasName.FIRST..lasName.LAST
      LOOP
        losSQL := losSQL || '''' || lasName(ind) || ''',';
      END LOOP;
      losSQL := RTRIM(losSQL,',') || ') ORDER BY TABLE_NAME';

      cursor_id := DBMS_SQL.OPEN_CURSOR;
      DBMS_SQL.PARSE(cursor_id, losSQL, DBMS_SQL.NATIVE);
      DBMS_SQL.DEFINE_ARRAY(cursor_id,1,lasBaseNames,50,1);
      ret_val := DBMS_SQL.EXECUTE(cursor_id);
      ret_val := DBMS_SQL.FETCH_ROWS(cursor_id);
      DBMS_SQL.COLUMN_VALUE(cursor_id,1,lasBaseNames);
      DBMS_SQL.CLOSE_CURSOR(cursor_id);

      -- Order the list of the FU table names in accordance with the order
      -- of the list of table names the created view based on.
      FOR BaseNameInd IN lasBaseNames.FIRST..lasBaseNames.LAST
      LOOP
        FOR NameInd IN lasName.FIRST..lasName.LAST
        LOOP
          IF lasBaseNames(BaseNameInd) = lasName(NameInd) THEN
            lasFUTableNameTmp(BaseNameInd) := lasFUTableName(NameInd);
            lasNameTmp(BaseNameInd) := lasBaseNames(BaseNameInd);
            EXIT;
          END IF;
        END LOOP;
      END LOOP;

      lasFUTableName := lasFUTableNameTmp;
      lasName := lasNameTmp;
    END IF;

    --  Get SQL statement for the view creation.
    lonIndex := 1;
    IF pionViewNumber = 1
    THEN
       lasDynamicString(lonIndex) := 'CREATE OR REPLACE VIEW UDR_LT_ST AS ';
    ELSIF pionViewNumber = 2
    THEN
       lasDynamicString(lonIndex) := 'CREATE OR REPLACE VIEW BCH_UDR_LT_ST AS ';
    END IF;
    FOR ind IN lasName.FIRST..lasName.LAST
    LOOP
      lonIndex := lonIndex + 1;
      lasDynamicString(lonIndex) := 'SELECT ''' || lasFUTableName(ind) || ''' TABLE_NAME';
      FOR rec IN (SELECT COLUMN_NAME
                  FROM USER_TAB_COLUMNS a
                  WHERE TABLE_NAME = lasName(ind)
                  ORDER BY COLUMN_NAME)
      LOOP
        lonIndex := lonIndex + 1;
        lasDynamicString(lonIndex) := ',' || rec.COLUMN_NAME;
      END LOOP;
      lonIndex := lonIndex + 1;
      lasDynamicString(lonIndex) := 'FROM ' || lasName(ind);
      lonIndex := lonIndex + 1;
      lasDynamicString(lonIndex) := 'UNION ALL';
    END LOOP;
    lasDynamicString(lonIndex) := 'WITH READ ONLY';
    lonIndex := lonIndex + 1;
    lasDynamicString(lonIndex) := '/';
    lasDDLStatements(1) := lasDynamicString;

    --  Get SQL statement for the granting of privileges on the created view.
    lonIndex := 1;
    lasDynamicString.DELETE;
    IF pionViewNumber = 1
    THEN
       lasDynamicString(lonIndex) := 'CALL DabUtil.GrantPriv(''UDR_LT_ST'', ''BSCS_ROLE'', ''SELECT'')';
    ELSIF pionViewNumber = 2
    THEN
       lasDynamicString(lonIndex) := 'CALL DabUtil.GrantPriv(''BCH_UDR_LT_ST'', ''BSCS_ROLE'', ''SELECT'')';
    END IF;
    lonIndex := lonIndex + 1;
    lasDynamicString(lonIndex) := '/';
    lasDDLStatements(2) := lasDynamicString;

    --  Get SQL statement for the creation of the public synonym for the created view.
    lonIndex := 1;
    lasDynamicString.DELETE;
    IF pionViewNumber = 1
    THEN
       lasDynamicString(lonIndex) := 'CALL DabUtil.CreateSynonym (''PUBLIC'',''RTX'',''UDR_LT_ST'')';
    ELSIF pionViewNumber = 2
    THEN
       lasDynamicString(lonIndex) := 'CALL DabUtil.CreateSynonym (''PUBLIC'',''RTX_BCH'',''BCH_UDR_LT_ST'')';
    END IF;
    lonIndex := lonIndex + 1;
    lasDynamicString(lonIndex) := '/';
    lasDDLStatements(3) := lasDynamicString;

    --  Apply generated statement for table creating
    ApplyDDLString (
                      piasDDLStatements => lasDDLStatements
                     ,piobExecute  => piobExecute
                   );
    IF piobExecute
    THEN
      DBMS_OUTPUT.PUT_LINE(RPAD('*',80,'*'));
       IF pionViewNumber = 1
       THEN
          CallInfo('View UDR_LT_ST created successfully.');
       ELSIF pionViewNumber = 2
       THEN
          CallInfo('View BCH_UDR_LT_ST created successfully.');
       END IF;
    END IF;

  EXCEPTION
    WHEN OTHERS
    THEN
      CallError('Create UDR view.');
  END CreateView_UDR;



