/*
** =====================================================================
**
** Procedure: RecreateIndexConstraint
** Purpose: This procedure creates a constraint or index  according
**          to a constraint description or returns a corresponding
**          statement to the STDOUT.
** Note: If the input parameter piosTableName is NOT NULL, then the
**       desired index or constraint is created just for the given
**       table, otherwise it is created for all tables belonging to
**       the context key identified by the input parameter piosConsDesc.
** =====================================================================
*/
  PROCEDURE RecreateIndexConstraint (
--  Constraint description
    piosConsDesc           VARCHAR2

--  Table name
   ,piosTableName          VARCHAR2 DEFAULT NULL

--  Automatic/Manual mode
--  TRUE - An SQL statement is executed
--  FALSE - Output of an SQL statement (without executing)
   ,piobExecute            BOOLEAN     DEFAULT FALSE
                                     )
  IS
    lasDDLStatements       GEN_UDR_SERV.tasDDLStatements;
  BEGIN
    --  Check presence of mandatory input parameter
    IF piosConsDesc IS NULL
    THEN
      CallError( 'Input parameter is NULL.' );
    END IF;

    --  Generate SQL statements for the table,
    --  constraints, indexes and comments creating
    GEN_UDR_SERV.IndexConstraintDDL (
			                             piosConsDesc      => UPPER(piosConsDesc)
			                            ,piosTableName     => UPPER(piosTableName)
                                        ,poasDDLStatements => lasDDLStatements
                                    );

    --  Apply generated statement for table creating
    ApplyDDLString (
                      piasDDLStatements => lasDDLStatements
                     ,piobExecute  => piobExecute
                   );
  EXCEPTION
	   WHEN OTHERS
	   THEN
	      CallError('RecreateIndexConstraint Constraint description: ' || UPPER(piosConsDesc) || ' failed.');
   END RecreateIndexConstraint;

